    document.addEventListener("DOMContentLoaded", function() {
        var active = false;
        var lazyLoad = function() {
            if (active === false) {
                active = true;
                var lazyImages = [].slice.call(document.querySelectorAll('img[data-src]'));
                setTimeout(function() {
                    lazyImages.forEach(function(lazyImage) {
                        if ((lazyImage.getBoundingClientRect().top <= window.innerHeight
                            && lazyImage.getBoundingClientRect().top > 0
                            && lazyImage.getBoundingClientRect().bottom >= 0)
                            && getComputedStyle(lazyImage).display !== "none") {

                            lazyImage.src = lazyImage.dataset.src;
                            lazyImage.srcset = lazyImage.dataset.srcset;
                            delete lazyImage.dataset.src;
                            delete lazyImage.dataset.srcset;
                            //removeAttr('data-src').removeAttribute('data-srcset');
                            //lazyImage.classList.remove("lazy");

                            lazyImages = lazyImages.filter(function(image) {
                                return image !== lazyImage;
                            });

                            if (lazyImages.length === 0) {
                                document.removeEventListener("scroll", lazyLoad);
                                window.removeEventListener("resize", lazyLoad);
                                window.removeEventListener("orientationchange", lazyLoad);
                            }
                        }
                    });

                    active = false;
                }, 200);
            }
        };

        window.onload = lazyLoad;
        document.addEventListener("scroll", lazyLoad);
        window.addEventListener("resize", lazyLoad);
        window.addEventListener("orientationchange", lazyLoad);
        document.querySelectorAll('a[data-toggle=tab]').forEach(function(tabsclick) {
            tabsclick.addEventListener("touchstart", lazyLoad);
            tabsclick.addEventListener("click", lazyLoad);
        });
    });
</script>
